
public class Iteration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;  //Initialisation..
		while(i<=5)  // condition
		{
			System.out.println("hi");
		    i++;  //increment or decrement operator
		}
		
		int j=1;
		do {
			System.out.println("hello");
			j++;
		}
		while(j>8);

	}

}
