
public class OperatorDemo {
	/*
	 * Arithmetic operator --> +,-,*,/,%
	 * Bitwise operator
	 * Relational operator
	 * Logical  operator
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int m=6,n=4;
       int r1=m+n;
       int r2=m-n;
       int r3=m*n;
       double r4=(double)m/n;
      System.out.println(r1);
      System.out.println(r2);
      System.out.println(r3);
      System.out.println(r4);
	}

}
